import React, { useState, useContext, useEffect } from 'react';
import useSWR from 'swr';

import Items from './Items.js'
import SLAs from './SLAs.js'
import ActiveAbilities from './ActiveAbilities.js'
import SpellInfo from './SpellInfo.js'
import PrepSpells from './PrepSpells.js'
import Spells from './Spells.js'
import PassiveAbilities from './PassiveAbilities.js'
import * as Navbar from './Navbars.js'
import AbilityScores from './AbilityScores.js'

import './dnd.css';

function rollDice(size, mod, use){
  const dice = Math.floor((Math.random() * size) +1)
  function edgeRollClassAssignment(dice){
    if(dice === 1){
      return 'natOne'
    }else if(dice === 20){
      return 'natTwenty'
    }else{
      return null
    }
  }
  const result =
  <span>
    <span id='rollUse'>{use}</span>
    <div id='rollTopLine'>
      <span id='rollTotal' className={edgeRollClassAssignment(dice)}>{dice}</span> <span id='modTotal'> + {mod} =</span>
    </div>
    <p id='rollModTotal'>{dice + mod}</p>
  </span>
  return result
}
export const ReadTossDice = React.createContext(null)
export const GetSetDisplay = React.createContext(null)
export const GetSetDisplayTwo = React.createContext(null)
export const ToggleInfo = React.createContext(null)
export const Selection = React.createContext(null)
export const Character = React.createContext(null)
